import React, { useState, useEffect } from 'react';
import { 
    Users, Trophy, Play, Settings, Plus, X, 
    Zap, CheckCircle, ArrowRight, Minus, Lock, Save, Rocket, RefreshCw, RotateCcw
} from 'lucide-react';

export default function App() {
  const [vistaActual, setVistaActual] = useState('AJUSTES');
  const [nombreTorneo, setNombreTorneo] = useState('Torneo de Dominó Live');
  const [codigoAcceso, setCodigoAcceso] = useState('');
  const [equipos, setEquipos] = useState([]);
  const [nuevoEquipo, setNuevoEquipo] = useState('');
  const [mesas, setMesas] = useState([]);
  const [ranking, setRanking] = useState([]);
  const [rondaActual, setRondaActual] = useState(1);
  const [totalRondas, setTotalRondas] = useState(2);
  const [metaPuntos, setMetaPuntos] = useState(100);
  const [torneoIniciado, setTorneoIniciado] = useState(false);

  // Inicializar ranking cuando se agregan equipos
  useEffect(() => {
    setRanking(equipos.map(name => ({ name, pg: 0, pp: 0, puntos: 0 })));
  }, [equipos]);

  const comenzarTorneo = () => {
    if (equipos.length < 2) return alert("Mínimo 2 equipos.");
    let mezclados = [...equipos].sort(() => Math.random() - 0.5);
    let nuevasMesas = [];
    for (let i = 0; i < mezclados.length; i += 2) {
      if (mezclados[i + 1]) {
        nuevasMesas.push({ id: i/2 + 1, e1: mezclados[i], e2: mezclados[i+1], puntos1: 0, puntos2: 0, finalizado: false });
      }
    }
    setMesas(nuevasMesas);
    setTorneoIniciado(true);
    setVistaActual('EQUIPOS');
  };

  const modificarPuntos = (mesaId, equipoNum, cant) => {
    setMesas(prev => prev.map(m => {
      if (m.id === mesaId && !m.finalizado) {
        let n1 = equipoNum === 1 ? m.puntos1 + cant : m.puntos1;
        let n2 = equipoNum === 2 ? m.puntos2 + cant : m.puntos2;
        n1 = Math.max(0, n1); n2 = Math.max(0, n2);

        const final = n1 >= metaPuntos || n2 >= metaPuntos;
        if (final) actualizarRanking(n1 >= metaPuntos ? m.e1 : m.e2, n1 >= metaPuntos ? m.e2 : m.e1, Math.max(n1, n2));

        return { ...m, puntos1: n1, puntos2: n2, finalizado: final };
      }
      return m;
    }));
  };

  const actualizarRanking = (ganador, perdedor, puntosGanador) => {
    setRanking(prev => prev.map(jugador => {
      if (jugador.name === ganador) return { ...jugador, pg: jugador.pg + 1, puntos: jugador.puntos + puntosGanador };
      if (jugador.name === perdedor) return { ...jugador, pp: jugador.pp + 1 };
      return jugador;
    }).sort((a, b) => b.pg - a.pg || b.puntos - a.puntos));
  };

  const navButtonStyle = (v) => ({
    backgroundColor: vistaActual === v ? '#ffa500' : 'transparent',
    border: 'none', color: vistaActual === v ? 'black' : '#8a8d91',
    padding: '12px 10px', borderRadius: '15px', cursor: 'pointer', fontSize: '10px',
    fontWeight: 'bold', display: 'flex', flexDirection: 'column', alignItems: 'center', gap: '5px', minWidth: '75px'
  });

  return (
    <div style={{ backgroundColor: '#0a0b0d', minHeight: '100vh', color: 'white', fontFamily: 'sans-serif' }}>
      <header style={{ backgroundColor: '#161b22', padding: '15px 0', borderBottom: '1px solid #30363d' }}>
        <div style={{textAlign:'center', marginBottom:'10px'}}>
           <h1 style={{ color: '#FFD700', fontSize: '18px', textTransform: 'uppercase', margin:0 }}>{nombreTorneo}</h1>
           <span style={{fontSize:'10px', color:'#8a8d91'}}>R{rondaActual}/{totalRondas} • META: {metaPuntos} PTS</span>
        </div>
        <nav style={{ display: 'flex', justifyContent: 'center', gap: '5px' }}>
          <button onClick={() => setVistaActual('MESAS')} style={navButtonStyle('MESAS')}><Play size={20} fill={vistaActual==='MESAS'?'black':'none'}/> CONTADOR</button>
          <button onClick={() => setVistaActual('RANKING')} style={navButtonStyle('RANKING')}><Trophy size={20}/> RANKING</button>
          <button onClick={() => setVistaActual('EQUIPOS')} style={navButtonStyle('EQUIPOS')}><Users size={20}/> Vs</button>
          <button onClick={() => setVistaActual('AJUSTES')} style={navButtonStyle('AJUSTES')}><Settings size={20}/> AJUSTES</button>
        </nav>
      </header>

      <main style={{ padding: '15px', maxWidth: '500px', margin: '0 auto' }}>
        {vistaActual === 'RANKING' && (
          <div style={{backgroundColor:'#161b22', borderRadius:'15px', overflow:'hidden', border:'1px solid #30363d'}}>
            <table style={{width:'100%', borderCollapse:'collapse', textAlign:'left'}}>
              <thead>
                <tr style={{backgroundColor:'#0d1117', color:'#ffa500', fontSize:'12px'}}>
                  <th style={{padding:'12px'}}>EQUIPO</th>
                  <th style={{padding:'12px'}}>PG</th>
                  <th style={{padding:'12px'}}>PP</th>
                  <th style={{padding:'12px'}}>PTOS</th>
                </tr>
              </thead>
              <tbody>
                {ranking.map((j, i) => (
                  <tr key={i} style={{borderBottom:'1px solid #30363d', fontSize:'14px'}}>
                    <td style={{padding:'12px'}}>{i+1}. {j.name}</td>
                    <td style={{padding:'12px', color:'#4caf50'}}>{j.pg}</td>
                    <td style={{padding:'12px', color:'#f44336'}}>{j.pp}</td>
                    <td style={{padding:'12px', fontWeight:'bold'}}>{j.puntos}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}

        {vistaActual === 'MESAS' && (
          <div style={{display:'flex', flexDirection:'column', gap:'15px'}}>
            {mesas.map(m => (
              <div key={m.id} style={{border: m.finalizado ? '2px solid #4caf50' : '1px solid #30363d', borderRadius:'20px', padding:'15px', backgroundColor:'#161b22'}}>
                <div style={{display:'flex', justifyContent:'space-between', fontSize:'11px', marginBottom:'10px'}}>
                  <span style={{color:'#8a8d91'}}>MESA #{m.id}</span>
                  {m.finalizado && <span style={{color:'#4caf50', fontWeight:'bold'}}>FINALIZADO</span>}
                </div>
                <div style={{display:'flex', justifyContent:'space-around', alignItems:'center'}}>
                  <div style={{textAlign:'center'}}>
                    <div style={{fontSize:'12px', marginBottom:'5px'}}>{m.e1}</div>
                    <div style={{fontSize:'35px', fontWeight:'bold'}}>{m.puntos1}</div>
                    {!m.finalizado && <button onClick={() => modificarPuntos(m.id, 1, 1)} style={btnSumar}><Plus/></button>}
                  </div>
                  <div style={{fontSize:'10px', color:'#30363d'}}>VS</div>
                  <div style={{textAlign:'center'}}>
                    <div style={{fontSize:'12px', marginBottom:'5px'}}>{m.e2}</div>
                    <div style={{fontSize:'35px', fontWeight:'bold'}}>{m.puntos2}</div>
                    {!m.finalizado && <button onClick={() => modificarPuntos(m.id, 2, 1)} style={btnSumar}><Plus/></button>}
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}

        {vistaActual === 'AJUSTES' && (
          <div style={{display:'flex', flexDirection:'column', gap:'15px'}}>
            <section style={sectionStyle}>
              <h3 style={sectionTitle}><Users size={16}/> REGISTRO</h3>
              <div style={{display:'flex', gap:'10px'}}>
                <input type="text" value={nuevoEquipo} onChange={(e) => setNuevoEquipo(e.target.value)} placeholder="Nombre equipo..." style={inputStyle}/>
                <button onClick={() => {if(nuevoEquipo){setEquipos([...equipos, nuevoEquipo]); setNuevoEquipo('');}}} style={{backgroundColor:'#4caf50', border:'none', borderRadius:'10px', color:'white', padding:'0 15px'}}><CheckCircle/></button>
              </div>
            </section>
            {!torneoIniciado && <button onClick={comenzarTorneo} style={btnStart}><Rocket size={20}/> COMENZAR TORNEO</button>}
          </div>
        )}

        {vistaActual === 'EQUIPOS' && (
          <div>
            <h2 style={{fontSize:'14px', color:'#ffa500', marginBottom:'10px'}}>ENFRENTAMIENTOS (Vs)</h2>
            {mesas.map(m => (
              <div key={m.id} style={cardVs}>
                <span>{m.e1} vs {m.e2}</span>
                <ArrowRight size={16} color="#ffa500" onClick={() => setVistaActual('MESAS')}/>
              </div>
            ))}
          </div>
        )}
      </main>
    </div>
  );
}

const sectionStyle = { backgroundColor: '#161b22', padding: '15px', borderRadius: '15px', border: '1px solid #30363d' };
const sectionTitle = { fontSize: '12px', color: '#ffa500', marginTop: 0, marginBottom: '10px', display: 'flex', alignItems: 'center', gap: '8px' };
const inputStyle = { width: '100%', padding: '10px', backgroundColor: '#0d1117', border: '1px solid #30363d', borderRadius: '10px', color: 'white' };
const btnSumar = { backgroundColor: '#1a4a2e', border: 'none', color: 'white', padding: '8px 15px', borderRadius: '8px', marginTop: '10px' };
const btnStart = { width: '100%', padding: '15px', backgroundColor: '#4caf50', border: 'none', borderRadius: '15px', color: 'white', fontWeight: 'bold', display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '10px' };
const cardVs = { backgroundColor:'#161b22', padding:'15px', borderRadius:'12px', marginBottom:'8px', border:'1px solid #30363d', display:'flex', justifyContent:'space-between' };
