import React, { useState, useEffect } from 'react';
import { 
    Users, Trophy, Play, Settings, Plus, X, 
    Zap, CheckCircle, ArrowRight, Minus, Clock, RefreshCw, AlertTriangle
} from 'lucide-react';

export default function App() {
  const [vistaActual, setVistaActual] = useState('AJUSTES');
  const [nombreTorneo, setNombreTorneo] = useState('Torneo de Dominó Live');
  const [equipos, setEquipos] = useState([]);
  const [nuevoEquipo, setNuevoEquipo] = useState('');
  const [mesas, setMesas] = useState([]);
  const [rondaActual, setRondaActual] = useState(1);
  const [totalRondas, setTotalRondas] = useState(2);
  const [metaPuntos, setMetaPuntos] = useState(100);
  const [tiempoMinutos, setTiempoMinutos] = useState(2);

  useEffect(() => {
    if (equipos.length < 2) {
      setMesas([]);
      return;
    }
    let mezclados = [...equipos].sort(() => Math.random() - 0.5);
    let nuevasMesas = [];
    for (let i = 0; i < mezclados.length; i += 2) {
      if (mezclados[i + 1]) {
        nuevasMesas.push({ 
          id: Math.floor(i/2) + 1, 
          e1: mezclados[i], 
          e2: mezclados[i+1],
          puntos1: 0, 
          puntos2: 0 
        });
      }
    }
    setMesas(nuevasMesas);
  }, [equipos]);

  const reiniciarTodo = () => {
    if(window.confirm("¿ESTÁS SEGURO? Se borrarán todos los equipos, puntos y rondas.")) {
      setEquipos([]);
      setMesas([]);
      setRondaActual(1);
      setNombreTorneo("Torneo de Dominó Live");
    }
  };

  const navButtonStyle = (v) => ({
    backgroundColor: vistaActual === v ? '#ffa500' : 'transparent',
    border: 'none', color: vistaActual === v ? 'black' : '#8a8d91',
    padding: '12px 10px', borderRadius: '15px', cursor: 'pointer', fontSize: '10px',
    fontWeight: 'bold', display: 'flex', flexDirection: 'column', alignItems: 'center', gap: '5px', minWidth: '70px'
  });

  return (
    <div style={{ backgroundColor: '#0a0b0d', minHeight: '100vh', color: 'white', fontFamily: 'sans-serif' }}>
      <header style={{ backgroundColor: '#161b22', padding: '15px 0', borderBottom: '1px solid #30363d' }}>
        <h1 style={{ textAlign: 'center', color: '#FFD700', fontSize: '18px', textTransform: 'uppercase', marginBottom: '15px' }}>{nombreTorneo}</h1>
        <nav style={{ display: 'flex', justifyContent: 'center', gap: '8px' }}>
          <button onClick={() => setVistaActual('MESAS')} style={navButtonStyle('MESAS')}><Play size={20}/> CONTADOR</button>
          <button onClick={() => setVistaActual('RANKING')} style={navButtonStyle('RANKING')}><Trophy size={20}/> RANKING</button>
          <button onClick={() => setVistaActual('EQUIPOS')} style={navButtonStyle('EQUIPOS')}><Users size={20}/> Vs</button>
          <button onClick={() => setVistaActual('AJUSTES')} style={navButtonStyle('AJUSTES')}><Settings size={20}/> AJUSTES</button>
        </nav>
      </header>

      <main style={{ padding: '15px', maxWidth: '500px', margin: '0 auto' }}>
        {vistaActual === 'AJUSTES' && (
          <div style={{ display: 'flex', flexDirection: 'column', gap: '20px' }}>

            {/* GESTIÓN DE RONDAS */}
            <section style={sectionStyle}>
              <h3 style={sectionTitle}><Zap size={16} color="#3b82f6"/> GESTIÓN DE RONDAS</h3>
              <div style={{ display: 'flex', justifyContent: 'space-between', padding: '15px', backgroundColor: '#0d1117', borderRadius: '10px', marginBottom: '10px' }}>
                <span>RONDA ACTUAL</span>
                <span style={{ fontWeight: 'bold', color: '#ffa500' }}>{rondaActual} / {totalRondas}</span>
              </div>
              <button style={{ width: '100%', padding: '12px', backgroundColor: '#2563eb', border: 'none', borderRadius: '10px', color: 'white', fontWeight: 'bold', display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '10px' }}>
                <RefreshCw size={18}/> INTENTAR GENERAR RONDA {rondaActual + 1}
              </button>
              <p style={{ fontSize: '10px', color: '#8b949e', textAlign: 'center', marginTop: '8px' }}>*La generación es automática al finalizar todas las mesas.</p>
            </section>

            {/* AJUSTES GENERALES */}
            <section style={sectionStyle}>
              <h3 style={sectionTitle}><Settings size={16} color="#8b949e"/> AJUSTES GENERALES</h3>
              <label style={labelStyle}>NOMBRE DEL TORNEO</label>
              <input type="text" value={nombreTorneo} onChange={(e) => setNombreTorneo(e.target.value)} style={inputStyle} />

              <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '10px', marginTop: '15px' }}>
                <div>
                  <label style={labelStyle}>TOTAL RONDAS</label>
                  <input type="number" value={totalRondas} onChange={(e) => setTotalRondas(e.target.value)} style={inputStyle} />
                </div>
                <div>
                  <label style={labelStyle}>TIEMPO (MIN)</label>
                  <input type="number" value={tiempoMinutos} onChange={(e) => setTiempoMinutos(e.target.value)} style={inputStyle} />
                </div>
              </div>

              <label style={{...labelStyle, marginTop: '15px'}}>META DE PUNTOS (Para ganar)</label>
              <input type="number" value={metaPuntos} onChange={(e) => setMetaPuntos(e.target.value)} style={inputStyle} />
            </section>

            {/* REGISTRO DE EQUIPOS */}
            <section style={sectionStyle}>
              <h3 style={sectionTitle}><Users size={16} color="#4caf50"/> REGISTRO DE EQUIPOS</h3>
              <div style={{ display: 'flex', gap: '10px' }}>
                <input type="text" value={nuevoEquipo} onChange={(e) => setNuevoEquipo(e.target.value)} placeholder="Nombre del equipo" style={{...inputStyle, marginTop: 0}} />
                <button onClick={() => { if(nuevoEquipo){ setEquipos([...equipos, nuevoEquipo]); setNuevoEquipo(''); }}} style={{ padding: '0 15px', backgroundColor: '#4caf50', border: 'none', borderRadius: '10px', color: 'white' }}><CheckCircle/></button>
              </div>
              <div style={{ marginTop: '10px' }}>
                {equipos.map((eq, i) => (
                  <div key={i} style={{ display: 'flex', justifyContent: 'space-between', padding: '10px 0', borderBottom: '1px solid #30363d' }}>
                    <span style={{ fontSize: '14px' }}>{i + 1}. {eq}</span>
                    <X size={16} color="#f44336" onClick={() => setEquipos(equipos.filter((_, idx) => idx !== i))}/>
                  </div>
                ))}
              </div>
            </section>

            {/* ZONA DE PELIGRO */}
            <section style={{ ...sectionStyle, border: '1px solid #441111' }}>
              <h3 style={{ ...sectionTitle, color: '#f44336' }}><AlertTriangle size={16}/> ZONA DE PELIGRO</h3>
              <button onClick={reiniciarTodo} style={{ width: '100%', padding: '15px', backgroundColor: '#441111', border: '1px solid #f44336', borderRadius: '10px', color: '#f44336', fontWeight: 'bold', cursor: 'pointer' }}>
                REINICIAR TORNEO COMPLETO
              </button>
            </section>

          </div>
        )}

        {/* Otras vistas se mantienen igual pero con este nuevo estilo de header */}
        {vistaActual === 'MESAS' && <div style={{textAlign:'center', color:'#8b949e', marginTop:'50px'}}>Pestaña de Contador Activa</div>}
      </main>
    </div>
  );
}

const sectionStyle = { backgroundColor: '#161b22', padding: '15px', borderRadius: '15px', border: '1px solid #30363d' };
const sectionTitle = { fontSize: '13px', color: '#ffa500', marginTop: 0, marginBottom: '15px', display: 'flex', alignItems: 'center', gap: '8px', textTransform: 'uppercase' };
const labelStyle = { fontSize: '10px', color: '#8b949e', display: 'block', marginBottom: '5px' };
const inputStyle = { width: '100%', padding: '12px', backgroundColor: '#0d1117', border: '1px solid #30363d', borderRadius: '10px', color: 'white', boxSizing: 'border-box' };
