import React, { useState, useEffect } from 'react';
import { 
    Users, Trophy, Play, Settings, Plus, X, 
    Zap, CheckCircle, ArrowRight, Minus
} from 'lucide-react';

export default function App() {
  const [vistaActual, setVistaActual] = useState('AJUSTES');
  const [nombreTorneo, setNombreTorneo] = useState('Torneo de Dominó Live');
  const [equipos, setEquipos] = useState([]);
  const [nuevoEquipo, setNuevoEquipo] = useState('');
  const [mesas, setMesas] = useState([]);
  const [mesaEnFocoId, setMesaEnFocoId] = useState(null);

  // Lógica de Sorteo y Creación de Mesas Automática
  useEffect(() => {
    if (equipos.length < 2) {
      setMesas([]);
      return;
    }
    let mezclados = [...equipos].sort(() => Math.random() - 0.5);
    let nuevasMesas = [];
    for (let i = 0; i < mezclados.length; i += 2) {
      if (mezclados[i + 1]) {
        nuevasMesas.push({ 
          id: Math.floor(i/2) + 1, 
          e1: mezclados[i], 
          e2: mezclados[i+1],
          puntos1: 0, 
          puntos2: 0 
        });
      }
    }
    setMesas(nuevasMesas);
  }, [equipos]);

  const modificarPuntos = (mesaId, equipoNum, delta) => {
    setMesas(prev => prev.map(m => {
      if (m.id === mesaId) {
        if (equipoNum === 1) return { ...m, puntos1: Math.max(0, m.puntos1 + delta) };
        return { ...m, puntos2: Math.max(0, m.puntos2 + delta) };
      }
      return m;
    }));
  };

  const navButtonStyle = (v) => ({
    backgroundColor: vistaActual === v ? '#ffa500' : 'transparent',
    border: 'none', color: vistaActual === v ? 'black' : '#8a8d91',
    padding: '10px', borderRadius: '12px', cursor: 'pointer', fontSize: '11px',
    fontWeight: 'bold', display: 'flex', flexDirection: 'column', alignItems: 'center', gap: '4px'
  });

  return (
    <div style={{ backgroundColor: '#0a0b0d', minHeight: '100vh', color: 'white', fontFamily: 'sans-serif' }}>
      <header style={{ backgroundColor: '#161b22', padding: '15px 0', borderBottom: '1px solid #30363d', textAlign: 'center' }}>
        <h1 style={{ color: '#FFD700', fontSize: '16px', marginBottom: '15px' }}>{nombreTorneo.toUpperCase()}</h1>
        <nav style={{ display: 'flex', justifyContent: 'space-around' }}>
          <button onClick={() => setVistaActual('MESAS')} style={navButtonStyle('MESAS')}><Play size={18}/> MESAS</button>
          <button onClick={() => setVistaActual('EQUIPOS')} style={navButtonStyle('EQUIPOS')}><Zap size={18}/> SORTEO</button>
          <button onClick={() => setVistaActual('AJUSTES')} style={navButtonStyle('AJUSTES')}><Settings size={18}/> AJUSTES</button>
        </nav>
      </header>

      <main style={{ padding: '20px', maxWidth: '500px', margin: '0 auto' }}>
        {vistaActual === 'EQUIPOS' && (
          <div>
            <h2 style={{color: '#ffa500', fontSize: '16px'}}>ENFRENTAMIENTOS</h2>
            {mesas.map((m) => (
              <div key={m.id} onClick={() => { setMesaEnFocoId(m.id); setVistaActual('MESAS'); }} 
                style={{backgroundColor: '#161b22', padding: '15px', borderRadius: '12px', marginBottom: '10px', border: '1px solid #30363d', cursor: 'pointer'}}>
                <div style={{display:'flex', justifyContent:'space-between', fontSize:'10px', color:'#FFD700', marginBottom:'8px'}}>
                  <span>MESA {m.id}</span>
                  <span>ANOTAR <ArrowRight size={10}/></span>
                </div>
                <div style={{display:'flex', justifyContent:'space-between', fontWeight:'bold'}}>
                  <span style={{color: '#4caf50'}}>{m.e1}</span>
                  <span style={{color: '#f44336'}}>{m.e2}</span>
                </div>
              </div>
            ))}
          </div>
        )}

        {vistaActual === 'MESAS' && (
          <div>
            {mesas.length === 0 ? <p style={{textAlign:'center', color:'#8b949e'}}>Registra equipos en AJUSTES.</p> :
              mesas.map((m) => (
                <div key={m.id} style={{backgroundColor: '#161b22', padding: '20px', borderRadius: '15px', marginBottom: '15px', border: mesaEnFocoId === m.id ? '2px solid #ffa500' : '1px solid #30363d'}}>
                  <h3 style={{textAlign:'center', color:'#ffa500', marginTop:0, fontSize:'14px'}}>MESA {m.id}</h3>
                  <div style={{display:'flex', justifyContent:'space-around', alignItems:'center'}}>
                    <div style={{textAlign:'center'}}>
                      <div style={{fontSize:'12px', color:'#4caf50', marginBottom:'5px'}}>{m.e1}</div>
                      <div style={{fontSize:'35px', fontWeight:'bold'}}>{m.puntos1}</div>
                      <div style={{display:'flex', gap:'5px', marginTop:'10px'}}>
                        <button onClick={() => modificarPuntos(m.id, 1, -1)} style={btnPunto}><Minus size={12}/></button>
                        <button onClick={() => modificarPuntos(m.id, 1, 1)} style={btnPunto}><Plus size={12}/></button>
                      </div>
                    </div>
                    <div style={{fontSize:'10px', color:'#30363d'}}>VS</div>
                    <div style={{textAlign:'center'}}>
                      <div style={{fontSize:'12px', color:'#f44336', marginBottom:'5px'}}>{m.e2}</div>
                      <div style={{fontSize:'35px', fontWeight:'bold'}}>{m.puntos2}</div>
                      <div style={{display:'flex', gap:'5px', marginTop:'10px'}}>
                        <button onClick={() => modificarPuntos(m.id, 2, -1)} style={btnPunto}><Minus size={12}/></button>
                        <button onClick={() => modificarPuntos(m.id, 2, 1)} style={btnPunto}><Plus size={12}/></button>
                      </div>
                    </div>
                  </div>
                </div>
              ))
            }
          </div>
        )}

        {vistaActual === 'AJUSTES' && (
          <div style={{ backgroundColor: '#161b22', padding: '20px', borderRadius: '15px' }}>
            <h2 style={{fontSize: '16px', color:'#ffa500'}}>REGISTRO DE EQUIPOS</h2>
            <div style={{display: 'flex', gap: '10px', marginTop: '15px' }}>
              <input type="text" value={nuevoEquipo} onChange={(e) => setNuevoEquipo(e.target.value)} 
                placeholder="Nombre del equipo" style={inputStyle} />
              <button onClick={() => { if(nuevoEquipo){ setEquipos([...equipos, nuevoEquipo]); setNuevoEquipo(''); }}} 
                style={{padding: '0 15px', backgroundColor: '#4caf50', border: 'none', borderRadius: '8px', color: 'white'}}><CheckCircle/></button>
            </div>
            <div style={{marginTop:'15px'}}>
              {equipos.map((eq, i) => (
                <div key={i} style={{display:'flex', justifyContent:'space-between', padding:'8px 0', borderBottom:'1px solid #30363d'}}>
                  <span style={{fontSize:'14px'}}>{eq}</span>
                  <X size={14} color="#f44336" onClick={() => setEquipos(equipos.filter((_, idx) => idx !== i))}/>
                </div>
              ))}
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

const btnPunto = { backgroundColor: '#30363d', border: 'none', color: 'white', padding: '10px', borderRadius: '8px', cursor: 'pointer' };
const inputStyle = { width: '100%', padding: '12px', backgroundColor: '#0d1117', border: '1px solid #30363d', borderRadius: '8px', color: 'white' };
