import React, { useState, useEffect } from 'react';
import { 
    Users, Trophy, Play, Settings, Plus, X, 
    Zap, CheckCircle, ArrowRight, Minus
} from 'lucide-react';

export default function App() {
  const [vistaActual, setVistaActual] = useState('AJUSTES');
  const [nombreTorneo, setNombreTorneo] = useState('Torneo de Dominó Live');
  const [equipos, setEquipos] = useState([]);
  const [nuevoEquipo, setNuevoEquipo] = useState('');
  const [mesas, setMesas] = useState([]);
  const [mesaEnFocoId, setMesaEnFocoId] = useState(null);

  useEffect(() => {
    if (equipos.length < 2) {
      setMesas([]);
      return;
    }
    let mezclados = [...equipos].sort(() => Math.random() - 0.5);
    let nuevasMesas = [];
    for (let i = 0; i < mezclados.length; i += 2) {
      if (mezclados[i + 1]) {
        nuevasMesas.push({ 
          id: Math.floor(i/2) + 1, 
          e1: mezclados[i], 
          e2: mezclados[i+1],
          puntos1: 0, 
          puntos2: 0 
        });
      }
    }
    setMesas(nuevasMesas);
  }, [equipos]);

  const modificarPuntos = (mesaId, equipoNum, delta) => {
    setMesas(prev => prev.map(m => {
      if (m.id === mesaId) {
        if (equipoNum === 1) return { ...m, puntos1: Math.max(0, m.puntos1 + delta) };
        return { ...m, puntos2: Math.max(0, m.puntos2 + delta) };
      }
      return m;
    }));
  };

  const navButtonStyle = (v) => ({
    backgroundColor: vistaActual === v ? '#ffa500' : 'transparent',
    border: 'none',
    color: vistaActual === v ? 'black' : '#8a8d91',
    padding: '12px 15px',
    borderRadius: '15px',
    cursor: 'pointer',
    fontSize: '10px',
    fontWeight: 'bold',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    gap: '8px',
    minWidth: '75px',
    transition: '0.3s'
  });

  return (
    <div style={{ backgroundColor: '#0a0b0d', minHeight: '100vh', color: 'white', fontFamily: 'sans-serif' }}>

      <header style={{ backgroundColor: '#161b22', padding: '20px 0', borderBottom: '1px solid #30363d' }}>
        <h1 style={{ 
          margin: '0 0 15px 0', 
          textAlign: 'center', 
          color: '#FFD700', 
          fontSize: '22px', 
          letterSpacing: '2px',
          textTransform: 'uppercase'
        }}>
          {nombreTorneo}
        </h1>

        <nav style={{ 
          display: 'flex', 
          justifyContent: 'center', 
          gap: '10px',
          padding: '0 10px'
        }}>
          {/* BOTÓN CAMBIADO A CONTADOR */}
          <button onClick={() => setVistaActual('MESAS')} style={navButtonStyle('MESAS')}>
            <Play size={22} fill={vistaActual === 'MESAS' ? 'black' : 'none'}/> 
            CONTADOR
          </button>

          <button onClick={() => setVistaActual('RANKING')} style={navButtonStyle('RANKING')}>
            <Trophy size={22}/> 
            RANKING
          </button>

          {/* BOTÓN CAMBIADO A Vs */}
          <button onClick={() => setVistaActual('EQUIPOS')} style={navButtonStyle('EQUIPOS')}>
            <Users size={22}/> 
            Vs
          </button>

          <button onClick={() => setVistaActual('AJUSTES')} style={navButtonStyle('AJUSTES')}>
            <Settings size={22}/> 
            AJUSTES
          </button>
        </nav>
      </header>

      <main style={{ padding: '20px', maxWidth: '500px', margin: '0 auto' }}>

        {vistaActual === 'MESAS' && (
          <div>
            {mesas.length === 0 ? (
               <div style={{textAlign:'center', marginTop:'50px', color:'#8b949e'}}>
                  <Play size={40} style={{opacity:0.3, marginBottom:'10px'}}/>
                  <p>No hay partidas activas.<br/>Registra equipos en Ajustes.</p>
               </div>
            ) : (
              mesas.map((m) => (
                <div key={m.id} style={{
                  backgroundColor: '#161b22', padding: '20px', borderRadius: '20px', marginBottom: '15px', 
                  border: mesaEnFocoId === m.id ? '2px solid #ffa500' : '1px solid #30363d'
                }}>
                  <h3 style={{textAlign:'center', color:'#ffa500', marginTop:0, fontSize:'14px'}}>MESA {m.id}</h3>
                  <div style={{display:'flex', justifyContent:'space-around', alignItems:'center'}}>
                    <div style={{textAlign:'center'}}>
                      <div style={{fontSize:'12px', color:'#4caf50', marginBottom:'5px'}}>{m.e1}</div>
                      <div style={{fontSize:'40px', fontWeight:'bold'}}>{m.puntos1}</div>
                      <div style={{display:'flex', gap:'8px', marginTop:'10px'}}>
                        <button onClick={() => modificarPuntos(m.id, 1, -1)} style={btnStyle}><Minus size={16}/></button>
                        <button onClick={() => modificarPuntos(m.id, 1, 1)} style={btnStyle}><Plus size={16}/></button>
                      </div>
                    </div>
                    <div style={{fontSize:'12px', color:'#30363d', fontWeight:'bold'}}>VS</div>
                    <div style={{textAlign:'center'}}>
                      <div style={{fontSize:'12px', color:'#f44336', marginBottom:'5px'}}>{m.e2}</div>
                      <div style={{fontSize:'40px', fontWeight:'bold'}}>{m.puntos2}</div>
                      <div style={{display:'flex', gap:'8px', marginTop:'10px'}}>
                        <button onClick={() => modificarPuntos(m.id, 2, -1)} style={btnStyle}><Minus size={16}/></button>
                        <button onClick={() => modificarPuntos(m.id, 2, 1)} style={btnStyle}><Plus size={16}/></button>
                      </div>
                    </div>
                  </div>
                </div>
              ))
            )}
          </div>
        )}

        {vistaActual === 'EQUIPOS' && (
          <div>
            <h2 style={{color: '#ffa500', fontSize: '18px', display:'flex', alignItems:'center', gap:'10px'}}><Zap/> ENFRENTAMIENTOS (Vs)</h2>
            {mesas.map((m) => (
              <div key={m.id} onClick={() => { setMesaEnFocoId(m.id); setVistaActual('MESAS'); }} 
                style={{backgroundColor: '#161b22', padding: '15px', borderRadius: '15px', marginBottom: '10px', border: '1px solid #30363d', cursor: 'pointer'}}>
                <div style={{display:'flex', justifyContent:'space-between', color:'#FFD700', fontSize:'11px', marginBottom:'10px'}}>
                  <span>MESA {m.id}</span>
                  <span>IR AL CONTADOR <ArrowRight size={14}/></span>
                </div>
                <div style={{display:'flex', justifyContent:'space-between', fontWeight:'bold'}}>
                  <span style={{color: '#4caf50'}}>{m.e1}</span>
                  <span style={{color: '#8b949e'}}>VS</span>
                  <span style={{color: '#f44336'}}>{m.e2}</span>
                </div>
              </div>
            ))}
          </div>
        )}

        {vistaActual === 'AJUSTES' && (
          <div style={{ backgroundColor: '#161b22', padding: '20px', borderRadius: '20px', border: '1px solid #30363d' }}>
            <h2 style={{fontSize: '18px', color:'#ffa500', marginBottom:'20px'}}>CONFIGURACIÓN</h2>
            <label style={{fontSize: '11px', color: '#8b949e'}}>NOMBRE DEL TORNEO</label>
            <input type="text" value={nombreTorneo} onChange={(e) => setNombreTorneo(e.target.value)} style={inputStyle} />

            <div style={{marginTop: '25px', padding: '15px', border: '1px solid #ffa500', borderRadius: '15px', backgroundColor: '#0d1117'}}>
              <label style={{color: '#ffa500', fontWeight: 'bold', fontSize: '12px'}}>REGISTRO DE EQUIPOS</label>
              <div style={{display: 'flex', gap: '10px', marginTop: '10px' }}>
                <input type="text" value={nuevoEquipo} onChange={(e) => setNuevoEquipo(e.target.value)} placeholder="Nombre del equipo" style={{...inputStyle, marginTop:0}} />
                <button onClick={() => { if(nuevoEquipo){ setEquipos([...equipos, nuevoEquipo]); setNuevoEquipo(''); }}} style={{padding:'0 15px', backgroundColor:'#4caf50', border:'none', borderRadius:'10px', color:'white'}}><CheckCircle/></button>
              </div>
              <div style={{marginTop:'15px'}}>
                {equipos.map((eq, i) => (
                  <div key={i} style={{display:'flex', justifyContent:'space-between', padding:'10px 0', borderBottom:'1px solid #30363d', fontSize:'14px'}}>
                    <span>{i+1}. {eq}</span>
                    <X size={16} color="#f44336" onClick={() => setEquipos(equipos.filter((_, idx) => idx !== i))}/>
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}

        {vistaActual === 'RANKING' && (
          <div style={{textAlign:'center', marginTop:'50px', color:'#8a8d91'}}>
            <Trophy size={50} style={{marginBottom:'15px', opacity:0.3}}/>
            <p>El ranking se generará automáticamente conforme terminen los partidos.</p>
          </div>
        )}
      </main>
    </div>
  );
}

const btnStyle = { backgroundColor: '#30363d', border: 'none', color: 'white', padding: '12px', borderRadius: '10px', cursor: 'pointer' };
const inputStyle = { width: '100%', padding: '12px', marginTop: '8px', backgroundColor: '#0d1117', border: '1px solid #30363d', borderRadius: '10px', color: 'white', boxSizing:'border-box' };
