import React, { useState, useEffect } from 'react';
import { 
    Users, Trophy, Play, Settings, Plus, X, 
    Zap, CheckCircle, ArrowRight, Minus, Lock, Save, Rocket, RefreshCw, AlertTriangle, RotateCcw
} from 'lucide-react';

export default function App() {
  const [vistaActual, setVistaActual] = useState('AJUSTES');
  const [nombreTorneo, setNombreTorneo] = useState('Torneo de Dominó Live');
  const [codigoAcceso, setCodigoAcceso] = useState('');
  const [formatoTorneo, setFormatoTorneo] = useState('Automático (Aleatorio)');
  const [equipos, setEquipos] = useState([]);
  const [nuevoEquipo, setNuevoEquipo] = useState('');
  const [mesas, setMesas] = useState([]);
  const [rondaActual, setRondaActual] = useState(1);
  const [totalRondas, setTotalRondas] = useState(2);
  const [metaPuntos, setMetaPuntos] = useState(100);
  const [tiempoMinutos, setTiempoMinutos] = useState(2);
  const [torneoIniciado, setTorneoIniciado] = useState(false);

  // Generar partidas automáticas basadas en los equipos registrados
  const comenzarTorneo = () => {
    if (equipos.length < 2) {
      alert("Necesitas al menos 2 equipos para comenzar.");
      return;
    }
    let mezclados = [...equipos].sort(() => Math.random() - 0.5);
    let nuevasMesas = [];
    for (let i = 0; i < mezclados.length; i += 2) {
      if (mezclados[i + 1]) {
        nuevasMesas.push({ 
          id: i / 2 + 1, 
          e1: mezclados[i], 
          e2: mezclados[i+1],
          puntos1: 0, 
          puntos2: 0,
          historial: [],
          finalizado: false
        });
      }
    }
    setMesas(nuevasMesas);
    setTorneoIniciado(true);
    setVistaActual('EQUIPOS'); // Va a la pestaña "Vs"
  };

  const modificarPuntos = (mesaId, equipoNum, cantidad) => {
    setMesas(prev => prev.map(m => {
      if (m.id === mesaId && !m.finalizado) {
        const n1 = equipoNum === 1 ? m.puntos1 + cantidad : m.puntos1;
        const n2 = equipoNum === 2 ? m.puntos2 + cantidad : m.puntos2;
        const final = n1 >= metaPuntos || n2 >= metaPuntos;
        return { ...m, puntos1: Math.max(0, n1), puntos2: Math.max(0, n2), finalizado: final };
      }
      return m;
    }));
  };

  const deshacerMano = (mesaId) => {
    // Lógica simplificada para ejemplo: resta 10 puntos (puedes ajustarlo a un historial real)
    modificarPuntos(mesaId, 1, -1); 
  };

  const navButtonStyle = (v) => ({
    backgroundColor: vistaActual === v ? '#ffa500' : 'transparent',
    border: 'none', color: vistaActual === v ? 'black' : '#8a8d91',
    padding: '12px 10px', borderRadius: '15px', cursor: 'pointer', fontSize: '10px',
    fontWeight: 'bold', display: 'flex', flexDirection: 'column', alignItems: 'center', gap: '5px', minWidth: '75px'
  });

  return (
    <div style={{ backgroundColor: '#0a0b0d', minHeight: '100vh', color: 'white', fontFamily: 'sans-serif' }}>
      <header style={{ backgroundColor: '#161b22', padding: '15px 0', borderBottom: '1px solid #30363d' }}>
        <div style={{textAlign:'center', marginBottom:'10px'}}>
           <h1 style={{ color: '#FFD700', fontSize: '18px', textTransform: 'uppercase', margin:0 }}>{nombreTorneo}</h1>
           <span style={{fontSize:'10px', color:'#8a8d91'}}>R{rondaActual}/{totalRondas} • {metaPuntos} PTS</span>
        </div>
        <nav style={{ display: 'flex', justifyContent: 'center', gap: '5px' }}>
          <button onClick={() => setVistaActual('MESAS')} style={navButtonStyle('MESAS')}><Play size={20} fill={vistaActual==='MESAS'?'black':'none'}/><span style={{marginTop:'2px'}}>CONTADOR</span></button>
          <button onClick={() => setVistaActual('RANKING')} style={navButtonStyle('RANKING')}><Trophy size={20}/><span>RANKING</span></button>
          <button onClick={() => setVistaActual('EQUIPOS')} style={navButtonStyle('EQUIPOS')}><Users size={20}/><span>Vs</span></button>
          <button onClick={() => setVistaActual('AJUSTES')} style={navButtonStyle('AJUSTES')}><Settings size={20}/><span>AJUSTES</span></button>
        </nav>
      </header>

      <main style={{ padding: '15px', maxWidth: '500px', margin: '0 auto' }}>

        {/* PESTAÑA CONTADOR (MESAS) */}
        {vistaActual === 'MESAS' && (
          <div style={{display:'flex', flexDirection:'column', gap:'20px'}}>
            {mesas.length === 0 ? <p style={{textAlign:'center', color:'#8a8d91', marginTop:'40px'}}>No hay partidas generadas. Ve a Ajustes y presiona Comenzar.</p> : 
            mesas.map(m => (
              <div key={m.id} style={{border:'2px solid #1a4a2e', borderRadius:'20px', padding:'15px', backgroundColor:'#0d1117'}}>
                <div style={{display:'flex', justifyContent:'space-between', marginBottom:'15px', fontSize:'11px'}}>
                  <span style={{backgroundColor:'#161b22', padding:'4px 8px', borderRadius:'5px'}}>R{rondaActual} | MESA #{m.id}</span>
                  <span style={{color:'#3b82f6', fontWeight:'bold'}}>GESTOR</span>
                </div>

                <button onClick={() => deshacerMano(m.id)} style={{width:'100%', backgroundColor:'#441a1a', border:'none', color:'#ff8a8a', padding:'8px', borderRadius:'10px', marginBottom:'20px', fontSize:'11px', display:'flex', alignItems:'center', justifyContent:'center', gap:'8px'}}>
                  <RotateCcw size={14}/> DESHACER ÚLTIMA MANO
                </button>

                <div style={{display:'flex', justifyContent:'space-around', alignItems:'center', position:'relative'}}>
                  <div style={{textAlign:'center'}}>
                    <div style={{fontSize:'14px', marginBottom:'5px'}}>{m.e1}</div>
                    <div style={{fontSize:'45px', fontWeight:'bold', color: m.puntos1 > m.puntos2 ? '#4caf50' : 'white'}}>{m.puntos1}</div>
                  </div>
                  <div style={{color:'#30363d', fontWeight:'bold'}}>VS</div>
                  <div style={{textAlign:'center'}}>
                    <div style={{fontSize:'14px', marginBottom:'5px'}}>{m.e2}</div>
                    <div style={{fontSize:'45px', fontWeight:'bold', color: m.puntos2 > m.puntos1 ? '#4caf50' : 'white'}}>{m.puntos2}</div>
                  </div>
                </div>

                {m.finalizado && (
                  <div style={{marginTop:'20px', backgroundColor:'#161b22', padding:'15px', borderRadius:'15px', textAlign:'center', border:'1px solid #4caf50'}}>
                    <div style={{color:'#4caf50', fontWeight:'bold', display:'flex', alignItems:'center', justifyContent:'center', gap:'10px'}}>
                      <Trophy size={16}/> GANADOR <Trophy size={16}/>
                    </div>
                    <div style={{fontSize:'20px', fontWeight:'bold', margin:'5px 0'}}>{m.puntos1 >= metaPuntos ? m.e1 : m.e2}</div>
                    <div style={{fontSize:'10px', color:'#8a8d91'}}>POR META DE PUNTOS ({metaPuntos} PTS)</div>
                  </div>
                )}

                <div style={{display:'flex', justifyContent:'center', gap:'20px', marginTop:'20px'}}>
                   <div style={{display:'flex', gap:'10px'}}>
                      <button onClick={() => modificarPuntos(m.id, 1, 1)} style={scoreBtn}><Plus/></button>
                      <button onClick={() => modificarPuntos(m.id, 2, 1)} style={scoreBtn}><Plus/></button>
                   </div>
                </div>
              </div>
            ))}
          </div>
        )}

        {/* PESTAÑA Vs (EQUIPOS/ENFRENTAMIENTOS) */}
        {vistaActual === 'EQUIPOS' && (
          <div>
            <h2 style={{fontSize:'16px', color:'#ffa500', marginBottom:'15px'}}>ENFRENTAMIENTOS ACTUALES</h2>
            {mesas.map(m => (
              <div key={m.id} style={{backgroundColor:'#161b22', padding:'15px', borderRadius:'15px', marginBottom:'10px', border:'1px solid #30363d', display:'flex', justifyContent:'space-between', alignItems:'center'}}>
                <div>
                  <div style={{fontSize:'10px', color:'#8a8d91'}}>MESA {m.id}</div>
                  <div style={{fontWeight:'bold'}}>{m.e1} <span style={{color:'#ffa500'}}>vs</span> {m.e2}</div>
                </div>
                <ArrowRight color="#ffa500" onClick={() => setVistaActual('MESAS')}/>
              </div>
            ))}
          </div>
        )}

        {/* PESTAÑA AJUSTES */}
        {vistaActual === 'AJUSTES' && (
          <div style={{ display: 'flex', flexDirection: 'column', gap: '20px' }}>
            <section style={sectionStyle}>
              <h3 style={sectionTitle}><Zap size={16} color="#3b82f6"/> GESTIÓN DE RONDAS</h3>
              <div style={{ display: 'flex', justifyContent: 'space-between', padding: '15px', backgroundColor: '#0d1117', borderRadius: '10px' }}>
                <span>RONDA ACTUAL</span>
                <span style={{ fontWeight: 'bold', color: '#ffa500' }}>{rondaActual} / {totalRondas}</span>
              </div>
              <button style={{ width: '100%', marginTop:'10px', padding: '12px', backgroundColor: '#2563eb', border: 'none', borderRadius: '10px', color: 'white', fontWeight: 'bold' }}>
                INTENTAR GENERAR RONDA {rondaActual + 1}
              </button>
            </section>

            <section style={sectionStyle}>
              <h3 style={sectionTitle}><Settings size={16}/> AJUSTES GENERALES</h3>
              <label style={labelStyle}>NOMBRE DEL TORNEO</label>
              <input type="text" value={nombreTorneo} onChange={(e) => setNombreTorneo(e.target.value)} style={inputStyle} />

              <label style={{...labelStyle, marginTop:'15px'}}>CÓDIGO DE ACCESO</label>
              <input type="text" placeholder="DEJAR VACÍO PARA ACCESO LIBRE" value={codigoAcceso} onChange={(e) => setCodigoAcceso(e.target.value)} style={inputStyle} />

              <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '10px', marginTop:'15px' }}>
                <div><label style={labelStyle}>TOTAL RONDAS</label><input type="number" value={totalRondas} onChange={(e) => setTotalRondas(parseInt(e.target.value))} style={inputStyle} /></div>
                <div><label style={labelStyle}>TIEMPO (MIN)</label><input type="number" value={tiempoMinutos} onChange={(e) => setTiempoMinutos(parseInt(e.target.value))} style={inputStyle} /></div>
              </div>

              <label style={{...labelStyle, marginTop:'15px'}}>META DE PUNTOS</label>
              <input type="number" value={metaPuntos} onChange={(e) => setMetaPuntos(parseInt(e.target.value))} style={inputStyle} />

              <label style={{...labelStyle, marginTop:'15px'}}>MÉTODO EMPAREJAMIENTO</label>
              <select value={formatoTorneo} onChange={(e) => setFormatoTorneo(e.target.value)} style={inputStyle}>
                <option>Automático (Aleatorio)</option>
                <option>Manual</option>
              </select>

              <button style={{ width: '100%', marginTop: '20px', padding: '15px', backgroundColor: '#ffa500', border: 'none', borderRadius: '10px', fontWeight: 'bold' }}>
                GUARDAR CAMBIOS
              </button>
            </section>

            <section style={sectionStyle}>
              <h3 style={sectionTitle}><Users size={16}/> REGISTRO DE EQUIPOS</h3>
              <div style={{ display: 'flex', gap: '10px' }}>
                <input type="text" value={nuevoEquipo} onChange={(e) => setNuevoEquipo(e.target.value)} placeholder="Nuevo equipo..." style={{...inputStyle, marginTop:0}} />
                <button onClick={() => { if(nuevoEquipo){ setEquipos([...equipos, nuevoEquipo]); setNuevoEquipo(''); }}} style={{ backgroundColor: '#4caf50', border: 'none', borderRadius: '10px', color: 'white', padding:'0 15px' }}><CheckCircle/></button>
              </div>
              <div style={{ marginTop: '10px' }}>
                {equipos.map((eq, i) => (
                  <div key={i} style={{ display: 'flex', justifyContent: 'space-between', padding: '8px 0', borderBottom: '1px solid #30363d' }}>
                    <span>{i + 1}. {eq}</span>
                    <X size={16} color="#f44336" onClick={() => setEquipos(equipos.filter((_, idx) => idx !== i))}/>
                  </div>
                ))}
              </div>
            </section>

            {!torneoIniciado && (
              <button onClick={comenzarTorneo} style={{ width: '100%', padding: '20px', backgroundColor: '#4caf50', border: 'none', borderRadius: '15px', color: 'white', fontWeight: 'bold', fontSize: '16px', display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '12px' }}>
                <Rocket size={24}/> COMENZAR TORNEO
              </button>
            )}

            <section style={{ ...sectionStyle, border: '1px solid #441111' }}>
              <button onClick={() => setTorneoIniciado(false)} style={{ width: '100%', padding: '15px', backgroundColor: '#441111', border: '1px solid #f44336', borderRadius: '10px', color: '#f44336', fontWeight: 'bold' }}>
                REINICIAR TORNEO COMPLETO
              </button>
            </section>
          </div>
        )}
      </main>
    </div>
  );
}

const sectionStyle = { backgroundColor: '#161b22', padding: '15px', borderRadius: '15px', border: '1px solid #30363d' };
const sectionTitle = { fontSize: '12px', color: '#ffa500', marginTop: 0, marginBottom: '15px', display: 'flex', alignItems: 'center', gap: '8px', textTransform: 'uppercase' };
const labelStyle = { fontSize: '10px', color: '#8a8d91', display: 'block', marginBottom: '5px' };
const inputStyle = { width: '100%', padding: '12px', backgroundColor: '#0d1117', border: '1px solid #30363d', borderRadius: '10px', color: 'white', boxSizing: 'border-box' };
const scoreBtn = { backgroundColor: '#1a4a2e', border: '1px solid #4caf50', color: 'white', padding: '10px 20px', borderRadius: '10px', cursor: 'pointer' };
