import React, { useState, useEffect } from 'react';
import { 
    Users, Trophy, Play, Settings, Plus, X, 
    Zap, CheckCircle, ArrowRight, Minus, Lock, Save, Rocket, RefreshCw, RotateCcw, Clock
} from 'lucide-react';

export default function App() {
  const [vistaActual, setVistaActual] = useState('AJUSTES');
  const [nombreTorneo, setNombreTorneo] = useState('Torneo de Dominó Live');
  const [equipos, setEquipos] = useState([]);
  const [nuevoEquipo, setNuevoEquipo] = useState('');
  const [mesas, setMesas] = useState([]);
  const [ranking, setRanking] = useState([]);
  const [rondaActual, setRondaActual] = useState(1);
  const [totalRondas, setTotalRondas] = useState(2);
  const [metaPuntos, setMetaPuntos] = useState(100);
  const [tiempoMinutos, setTiempoMinutos] = useState(20);
  const [segundosRestantes, setSegundosRestantes] = useState(0);
  const [torneoIniciado, setTorneoIniciado] = useState(false);

  // Lógica del Cronómetro
  useEffect(() => {
    let intervalo = null;
    if (torneoIniciado && segundosRestantes > 0) {
      intervalo = setInterval(() => {
        setSegundosRestantes((prev) => prev - 1);
      }, 1000);
    } else if (segundosRestantes === 0 && torneoIniciado) {
      clearInterval(intervalo);
    }
    return () => clearInterval(intervalo);
  }, [torneoIniciado, segundosRestantes]);

  const formatearTiempo = (segundos) => {
    const m = Math.floor(segundos / 60);
    const s = segundos % 60;
    return `${m}:${s < 10 ? '0' : ''}${s}`;
  };

  const comenzarTorneo = () => {
    if (equipos.length < 2) return alert("Mínimo 2 equipos.");
    let mezclados = [...equipos].sort(() => Math.random() - 0.5);
    let nuevasMesas = [];
    for (let i = 0; i < mezclados.length; i += 2) {
      if (mezclados[i + 1]) {
        nuevasMesas.push({ id: i/2 + 1, e1: mezclados[i], e2: mezclados[i+1], puntos1: 0, puntos2: 0, finalizado: false });
      }
    }
    setMesas(nuevasMesas);
    setSegundosRestantes(tiempoMinutos * 60);
    setTorneoIniciado(true);
    setVistaActual('MESAS');
  };

  const modificarPuntos = (mesaId, equipoNum, cant) => {
    setMesas(prev => prev.map(m => {
      if (m.id === mesaId && !m.finalizado) {
        let n1 = equipoNum === 1 ? m.puntos1 + cant : m.puntos1;
        let n2 = equipoNum === 2 ? m.puntos2 + cant : m.puntos2;
        n1 = Math.max(0, n1); n2 = Math.max(0, n2);

        const alcanzoMeta = n1 >= metaPuntos || n2 >= metaPuntos;
        // La mesa solo se finaliza si se alcanza la meta o si el usuario decide cerrarla tras el tiempo
        return { ...m, puntos1: n1, puntos2: n2, finalizado: alcanzoMeta };
      }
      return m;
    }));
  };

  const cerrarMesaManual = (mesaId) => {
    setMesas(prev => prev.map(m => {
        if (m.id === mesaId) {
            actualizarRanking(m.puntos1 > m.puntos2 ? m.e1 : m.e2, m.puntos1 > m.puntos2 ? m.e2 : m.e1, Math.max(m.puntos1, m.puntos2));
            return { ...m, finalizado: true };
        }
        return m;
    }));
  };

  const actualizarRanking = (ganador, perdedor, puntosGanador) => {
    setRanking(prev => {
        const nuevo = prev.map(jugador => {
            if (jugador.name === ganador) return { ...jugador, pg: jugador.pg + 1, puntos: jugador.puntos + puntosGanador };
            if (jugador.name === perdedor) return { ...jugador, pp: jugador.pp + 1 };
            return jugador;
        });
        return nuevo.sort((a, b) => b.pg - a.pg || b.puntos - a.puntos);
    });
  };

  const navButtonStyle = (v) => ({
    backgroundColor: vistaActual === v ? '#ffa500' : 'transparent',
    border: 'none', color: vistaActual === v ? 'black' : '#8a8d91',
    padding: '12px 10px', borderRadius: '15px', cursor: 'pointer', fontSize: '10px',
    fontWeight: 'bold', display: 'flex', flexDirection: 'column', alignItems: 'center', gap: '5px', minWidth: '70px'
  });

  return (
    <div style={{ backgroundColor: '#0a0b0d', minHeight: '100vh', color: 'white', fontFamily: 'sans-serif' }}>
      <header style={{ backgroundColor: '#161b22', padding: '15px 0', borderBottom: '1px solid #30363d' }}>
        <div style={{textAlign:'center', marginBottom:'10px'}}>
           <h1 style={{ color: '#FFD700', fontSize: '18px', textTransform: 'uppercase', margin:0 }}>{nombreTorneo}</h1>
           <div style={{display:'flex', justifyContent:'center', gap:'15px', marginTop:'5px'}}>
              <span style={{fontSize:'11px', color:'#8a8d91'}}>RONDA {rondaActual}</span>
              {torneoIniciado && (
                <span style={{fontSize:'11px', color: segundosRestantes < 60 ? '#f44336' : '#4caf50', fontWeight:'bold', display:'flex', alignItems:'center', gap:'5px'}}>
                   <Clock size={12}/> {formatearTiempo(segundosRestantes)}
                </span>
              )}
           </div>
        </div>
        <nav style={{ display: 'flex', justifyContent: 'center', gap: '5px' }}>
          <button onClick={() => setVistaActual('MESAS')} style={navButtonStyle('MESAS')}><Play size={20}/> CONTADOR</button>
          <button onClick={() => setVistaActual('RANKING')} style={navButtonStyle('RANKING')}><Trophy size={20}/> RANKING</button>
          <button onClick={() => setVistaActual('EQUIPOS')} style={navButtonStyle('EQUIPOS')}><Users size={20}/> Vs</button>
          <button onClick={() => setVistaActual('AJUSTES')} style={navButtonStyle('AJUSTES')}><Settings size={20}/> AJUSTES</button>
        </nav>
      </header>

      <main style={{ padding: '15px', maxWidth: '500px', margin: '0 auto' }}>

        {vistaActual === 'MESAS' && (
          <div style={{display:'flex', flexDirection:'column', gap:'15px'}}>
            {segundosRestantes === 0 && torneoIniciado && (
                <div style={{backgroundColor:'#441a1a', color:'#ff8a8a', padding:'10px', borderRadius:'10px', textAlign:'center', fontSize:'12px', fontWeight:'bold', border:'1px solid #f44336'}}>
                    ¡TIEMPO AGOTADO! JUEGUEN LA ÚLTIMA MANO Y CIERREN LA MESA.
                </div>
            )}
            {mesas.map(m => (
              <div key={m.id} style={{border: m.finalizado ? '2px solid #4caf50' : '1px solid #30363d', borderRadius:'20px', padding:'15px', backgroundColor:'#161b22'}}>
                <div style={{display:'flex', justifyContent:'space-between', fontSize:'11px', marginBottom:'10px'}}>
                  <span style={{color:'#8a8d91'}}>MESA #{m.id}</span>
                  {m.finalizado ? <span style={{color:'#4caf50', fontWeight:'bold'}}>MESA CERRADA</span> : (segundosRestantes === 0 ? <span style={{color:'#ffa500'}}>ESPERANDO CIERRE...</span> : null)}
                </div>

                <div style={{display:'flex', justifyContent:'space-around', alignItems:'center', marginBottom:'15px'}}>
                  <div style={{textAlign:'center'}}>
                    <div style={{fontSize:'13px', color:'#4caf50'}}>{m.e1}</div>
                    <div style={{fontSize:'40px', fontWeight:'bold'}}>{m.puntos1}</div>
                    {!m.finalizado && <button onClick={() => modificarPuntos(m.id, 1, 1)} style={btnControl}><Plus/></button>}
                  </div>
                  <div style={{fontSize:'12px', color:'#30363d'}}>VS</div>
                  <div style={{textAlign:'center'}}>
                    <div style={{fontSize:'13px', color:'#f44336'}}>{m.e2}</div>
                    <div style={{fontSize:'40px', fontWeight:'bold'}}>{m.puntos2}</div>
                    {!m.finalizado && <button onClick={() => modificarPuntos(m.id, 2, 1)} style={btnControl}><Plus/></button>}
                  </div>
                </div>

                {!m.finalizado && segundosRestantes === 0 && (
                    <button onClick={() => cerrarMesaManual(m.id)} style={{width:'100%', padding:'10px', backgroundColor:'#ffa500', border:'none', borderRadius:'10px', fontWeight:'bold', color:'black'}}>
                        TERMINAR PARTIDA Y GUARDAR
                    </button>
                )}
              </div>
            ))}
          </div>
        )}

        {/* RESTO DE VISTAS (RANKING, EQUIPOS, AJUSTES) SE MANTIENEN IGUAL */}
        {vistaActual === 'AJUSTES' && (
            <div style={{display:'flex', flexDirection:'column', gap:'15px'}}>
                <section style={sectionStyle}>
                    <h3 style={sectionTitle}><Settings size={16}/> AJUSTES GENERALES</h3>
                    <label style={labelStyle}>TIEMPO POR RONDA (MINUTOS)</label>
                    <input type="number" value={tiempoMinutos} onChange={(e) => setTiempoMinutos(parseInt(e.target.value))} style={inputStyle}/>
                    <label style={{...labelStyle, marginTop:'10px'}}>META DE PUNTOS</label>
                    <input type="number" value={metaPuntos} onChange={(e) => setMetaPuntos(parseInt(e.target.value))} style={inputStyle}/>
                </section>
                <section style={sectionStyle}>
                    <h3 style={sectionTitle}><Users size={16}/> REGISTRO DE EQUIPOS</h3>
                    <div style={{display:'flex', gap:'10px'}}>
                        <input type="text" value={nuevoEquipo} onChange={(e) => setNuevoEquipo(e.target.value)} placeholder="Nombre..." style={inputStyle}/>
                        <button onClick={() => {if(nuevoEquipo){setEquipos([...equipos, nuevoEquipo]); setRanking([...ranking, {name: nuevoEquipo, pg:0, pp:0, puntos:0}]); setNuevoEquipo('');}}} style={{backgroundColor:'#4caf50', border:'none', padding:'0 15px', borderRadius:'10px'}}><CheckCircle color="white"/></button>
                    </div>
                </section>
                {!torneoIniciado && <button onClick={comenzarTorneo} style={btnStart}><Rocket/> COMENZAR TORNEO</button>}
            </div>
        )}

        {vistaActual === 'RANKING' && (
            <div style={{backgroundColor:'#161b22', borderRadius:'15px', overflow:'hidden'}}>
                <table style={{width:'100%', textAlign:'left', borderCollapse:'collapse'}}>
                    <thead style={{backgroundColor:'#0d1117', color:'#ffa500', fontSize:'12px'}}>
                        <tr><th style={{padding:'12px'}}>EQUIPO</th><th style={{padding:'12px'}}>PG</th><th style={{padding:'12px'}}>PTOS</th></tr>
                    </thead>
                    <tbody>
                        {ranking.map((j, i) => (
                            <tr key={i} style={{borderBottom:'1px solid #30363d'}}>
                                <td style={{padding:'12px'}}>{i+1}. {j.name}</td>
                                <td style={{padding:'12px', color:'#4caf50'}}>{j.pg}</td>
                                <td style={{padding:'12px'}}>{j.puntos}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        )}
      </main>
    </div>
  );
}

const sectionStyle = { backgroundColor: '#161b22', padding: '15px', borderRadius: '15px', border: '1px solid #30363d' };
const sectionTitle = { fontSize: '12px', color: '#ffa500', marginTop: 0, marginBottom: '10px', display: 'flex', alignItems: 'center', gap: '8px' };
const inputStyle = { width: '100%', padding: '12px', backgroundColor: '#0d1117', border: '1px solid #30363d', borderRadius: '10px', color: 'white', boxSizing:'border-box' };
const labelStyle = { fontSize: '10px', color: '#8a8d91', display: 'block', marginBottom: '5px' };
const btnControl = { backgroundColor: '#1a4a2e', border: '1px solid #4caf50', color: 'white', padding: '10px 20px', borderRadius: '10px' };
const btnStart = { width: '100%', padding: '20px', backgroundColor: '#4caf50', border: 'none', borderRadius: '15px', color: 'white', fontWeight: 'bold', display: 'flex', justifyContent: 'center', gap: '10px' };
